import React, { useState } from 'react';

interface Question {
  id: number;
  title: string;
  description: string;
  examples: { input: string; output: string }[];
  constraints: string[];
}

const steps = [
  'question',
  'clarify',
  'bruteForce',
  'optimize',
  'code',
  'review',
];

function App() {
  const [step, setStep] = useState(0);
  const [question, setQuestion] = useState<Question | null>(null);
  const [clarifyInput, setClarifyInput] = useState('');
  const [clarifyResponse, setClarifyResponse] = useState('');
  const [bruteForceInput, setBruteForceInput] = useState('');
  const [bruteForceResponse, setBruteForceResponse] = useState('');
  const [optimizeInput, setOptimizeInput] = useState('');
  const [optimizeResponse, setOptimizeResponse] = useState('');
  const [codeInput, setCodeInput] = useState('');
  const [review, setReview] = useState<any>(null);

  const nextStep = () => setStep((s) => s + 1);

  React.useEffect(() => {
    fetch('/api/start-session', { method: 'POST' })
      .then((res) => res.json())
      .then((data) => setQuestion(data.question));
  }, []);

  const handleClarify = async () => {
    const res = await fetch('/api/clarify', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ user_input: clarifyInput }),
    });
    const data = await res.json();
    setClarifyResponse(data.response);
    nextStep();
  };

  const handleBruteForce = async () => {
    const res = await fetch('/api/brute-force', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ user_idea: bruteForceInput }),
    });
    const data = await res.json();
    setBruteForceResponse(data.response);
    nextStep();
  };

  const handleOptimize = async () => {
    const res = await fetch('/api/optimize', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ user_idea: optimizeInput }),
    });
    const data = await res.json();
    setOptimizeResponse(data.response);
    nextStep();
  };

  const handleCodeReview = async () => {
    const res = await fetch('/api/code-review', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        clarification: clarifyInput,
        brute_force: bruteForceInput,
        code: codeInput,
      }),
    });
    const data = await res.json();
    setReview(data.review);
    nextStep();
  };

  return (
    <div className="max-w-2xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">LeetCoach Interview Simulator</h1>
      {step === 0 && question && (
        <div>
          <h2 className="text-xl font-semibold">{question.title}</h2>
          <p className="mb-2">{question.description}</p>
          <div className="mb-2">
            <strong>Examples:</strong>
            {question.examples.map((ex, i) => (
              <div key={i} className="ml-4 text-sm">Input: {ex.input} | Output: {ex.output}</div>
            ))}
          </div>
          <div className="mb-2">
            <strong>Constraints:</strong>
            <ul className="list-disc ml-6">
              {question.constraints.map((c, i) => (
                <li key={i}>{c}</li>
              ))}
            </ul>
          </div>
          <button className="btn btn-primary mt-4" onClick={nextStep}>Start Interview</button>
        </div>
      )}
      {step === 1 && (
        <div>
          <h2 className="font-semibold">Step 1: Ask clarifying questions</h2>
          <textarea className="w-full border p-2" value={clarifyInput} onChange={e => setClarifyInput(e.target.value)} placeholder="Ask about input, constraints, etc." />
          <button className="btn btn-primary mt-2" onClick={handleClarify}>Submit</button>
          {clarifyResponse && <div className="mt-2 p-2 bg-gray-100">Interviewer: {clarifyResponse}</div>}
        </div>
      )}
      {step === 2 && (
        <div>
          <h2 className="font-semibold">Step 2: Explain brute-force solution</h2>
          <textarea className="w-full border p-2" value={bruteForceInput} onChange={e => setBruteForceInput(e.target.value)} placeholder="Describe your brute-force approach..." />
          <button className="btn btn-primary mt-2" onClick={handleBruteForce}>Submit</button>
          {bruteForceResponse && <div className="mt-2 p-2 bg-gray-100">Interviewer: {bruteForceResponse}</div>}
        </div>
      )}
      {step === 3 && (
        <div>
          <h2 className="font-semibold">Step 3: Explain optimized solution</h2>
          <textarea className="w-full border p-2" value={optimizeInput} onChange={e => setOptimizeInput(e.target.value)} placeholder="Describe your optimized approach..." />
          <button className="btn btn-primary mt-2" onClick={handleOptimize}>Submit</button>
          {optimizeResponse && <div className="mt-2 p-2 bg-gray-100">Interviewer: {optimizeResponse}</div>}
        </div>
      )}
      {step === 4 && (
        <div>
          <h2 className="font-semibold">Step 4: Write your code</h2>
          <textarea className="w-full border p-2 font-mono" rows={8} value={codeInput} onChange={e => setCodeInput(e.target.value)} placeholder="Write your code here..." />
          <button className="btn btn-primary mt-2" onClick={handleCodeReview}>Submit for Review</button>
        </div>
      )}
      {step === 5 && review && (
        <div>
          <h2 className="font-semibold">Step 5: Code Review</h2>
          <div className="p-2 bg-gray-100 mb-2">
            <div className="mb-2">
              <strong>Input Clarification:</strong> {review.clarification?.grade}/10<br/>
              <span className="text-sm">{review.clarification?.feedback}</span>
            </div>
            <div className="mb-2">
              <strong>Brute-force Idea:</strong> {review.brute_force?.grade}/10<br/>
              <span className="text-sm">{review.brute_force?.feedback}</span>
            </div>
            <div className="mb-2">
              <strong>Coding Solution:</strong> {review.coding?.grade}/10<br/>
              <span className="text-sm">{review.coding?.feedback}</span>
            </div>
            <div className="mb-2">
              <strong>Key Pointers:</strong> <span className="text-sm">{review.key_pointers}</span>
            </div>
          </div>
          <button className="btn btn-primary" onClick={() => window.location.reload()}>Restart</button>
        </div>
      )}
    </div>
  );
}

export default App;
